  computed: {
    faAngleDown() {
      return faAngleDown;
    },
    faBars() {
      return faBars;
    }
  },
  mounted: function() {
    this.ScreenSize = window.innerWidth;
    window.addEventListener("resize", () => {
      this.ScreenSize = window.innerWidth;
    });
  },
  methods: {
    ShowMenu() {
      this.RHide = !this.RHide;
    }
  }
};
